--FUNCTIONS
CREATE OR REPLACE FUNCTION add_ingredient(recipe integer, product_name varchar(60), id_category integer, amount numeric(6,2), unit unit_enum)
RETURNS void AS $$
DECLARE
  id integer;
BEGIN
  SELECT id_product INTO id FROM products WHERE name = product_name;
  IF id IS NULL THEN
	RETURNING id_product INTO id;
  END IF;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION add_all_ingredients(recipe integer, product_name varchar(60)[], id_category integer[], amount numeric(6,2)[], unit unit_enum[])
-- YOU NEED TO CAST ARRAYS!!! like that
-- add_all_ingredients(1, ARRAY['unsalted butter','lol']::varchar(60)[], ARRAY[7,3], ARRAY[250,100], ARRAY['g','l']::unit_enum[]);
RETURNS void AS $$
DECLARE
  id integer;
  i integer;
BEGIN
DELETE FROM recipes_products WHERE id_recipe=recipe;
FOR i IN 1..array_length(product_name, 1)
LOOP 
  PERFORM add_ingredient(recipe,product_name[i], id_category[i], amount[i],unit[i]);
END LOOP;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION update_recipe() RETURNS TRIGGER AS $update_recipe$
BEGIN
    NEW.added_at = OLD.added_at;
    RETURN NEW;
END;
$update_recipe$ LANGUAGE plpgsql;
CREATE TRIGGER update_recipe BEFORE UPDATE ON recipes
FOR EACH ROW EXECUTE PROCEDURE update_recipe();

