CREATE OR REPLACE FUNCTION last_modified(id_recipe_ int)
RETURNS timestamp AS
$$
  BEGIN
    RETURN COALESCE((SELECT MAX(archivised) FROM archival_recipes WHERE id_recipe_=id_recipe),(SELECT added_at FROM recipes WHERE id_recipe_=id_recipe));
  END;
$$LANGUAGE plpgsql;
CREATE OR REPLACE FUNCTION shopping_list(list int[])
RETURNS TABLE(id_product int, product_name varchar(100)) AS
$$
  BEGIN
  RETURN QUERY SELECT DISTINCT products.id_product, products.name 
  FROM products JOIN recipes_products USING(id_product)
  JOIN recipes USING(id_recipe) WHERE id_recipe IN (SELECT unnest(list));
  END;
$$LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION users_notes(id_recipe_ int, id_user_ int)   -- tu trzeba sprawdzić, czy użytkownik jest autorem przepisu
RETURNS TABLE(id_note int, body text) AS
$$
  BEGIN
  RETURN QUERY SELECT notes.id_note, notes.body FROM notes WHERE id_recipe=id_recipe_;
  END;
$$LANGUAGE plpgsql;
CREATE OR REPLACE FUNCTION tags_from_recipe(recipe integer)
RETURNS TABLE(tags varchar(60))  AS $$
DECLARE
BEGIN
  RETURN QUERY SELECT tag 
  FROM recipes_tags NATURAL JOIN tags
  WHERE id_recipe=recipe;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION products_from_recpie(recipe integer)
RETURNS TABLE(products varchar(60),amount numeric(6,2), unit unit_enum) AS $$
BEGIN
	RETURN QUERY SELECT products.name, recipes_products.amount,recipes_products.unit FROM recipes JOIN recipes_products USING(id_recipe)
	JOIN products USING(id_product) WHERE recipe=id_recipe;
END;
$$LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION restrictions_from_recipe(recipe integer)
RETURNS TABLE(restriction varchar(60))  AS $$
DECLARE
BEGIN
  RETURN QUERY SELECT DISTINCT restrictions.restriction 
  FROM restrictions JOIN products_restrictions ON restrictions.id_restriction=products_restrictions.id_restriction
    JOIN products ON products_restrictions.id_product=products.id_product 
    JOIN recipes_products ON products.id_product=recipes_products.id_product
    JOIN recipes ON recipes.id_recipe=recipes_products.id_recipe
  WHERE recipes.id_recipe=recipe;
END;
$$ LANGUAGE plpgsql;
CREATE OR REPLACE VIEW recipe_info AS 
SELECT recipes.name,id_user,username,shape,added_at,body,id_recipe,difficulty,preparation_time --we can add sth here
FROM recipes NATURAL JOIN users NATURAL JOIN forms; 

CREATE OR REPLACE FUNCTION basic_from_recipe(recipe integer)
RETURNS TABLE(name varchar(100),author varchar(40),author_id integer,shape shape_enum,added_at timestamp,body text[],difficulty difficulty_enum,preparation_time interval,likes bigint)  AS $$
DECLARE
BEGIN
  RETURN QUERY SELECT recipe_info.name,username,recipe_info.id_user,recipe_info.shape,recipe_info.added_at,recipe_info.body,recipe_info.difficulty,recipe_info.preparation_time, COUNT(*) FROM recipe_info 
  LEFT JOIN users_liked USING(id_recipe)
  WHERE id_recipe=recipe
  GROUP BY recipe_info.name,username,recipe_info.shape,recipe_info.added_at,recipe_info.body,recipe_info.difficulty,recipe_info.preparation_time,recipe_info.id_user;
END;
$$ LANGUAGE plpgsql;
CREATE OR REPLACE FUNCTION comments_from_comment(comment integer)
RETURNS TABLE(username varchar(40),body text,id_comment integer)  AS $$
DECLARE
BEGIN
  RETURN QUERY SELECT users.username,comments.body,comments.id_comment FROM comments JOIN users USING(id_user)
  WHERE id_parent=comment;
END;
$$ LANGUAGE plpgsql;
CREATE OR REPLACE FUNCTION comments_from_recipe(recipe integer) --returns only parent comments!
RETURNS TABLE(username varchar(40),body text,id_comment integer)  AS $$
DECLARE
BEGIN
  RETURN QUERY SELECT users.username,comments.body,comments.id_comment FROM comments JOIN users USING(id_user)
  WHERE id_recipe=recipe AND id_parent IS NULL;
END;
$$ LANGUAGE plpgsql;
CREATE OR REPLACE FUNCTION recipes_with_products(list varchar(60)[])
RETURNS TABLE(id_recipe int,name varchar(100))  AS $$
DECLARE
BEGIN
  RETURN QUERY 
  SELECT recipes.id_recipe, recipes.name 
  FROM recipes
  JOIN recipes_products USING(id_recipe)
  JOIN products USING(id_product)
  WHERE products.name IN (SELECT unnest(list))
  GROUP BY recipes.id_recipe
  HAVING COUNT(DISTINCT products.name)=array_length(list,1);
END;
$$ LANGUAGE plpgsql;

