CREATE OR REPLACE FUNCTION is_liked(recipe integer, id_use integer)
RETURNS boolean AS $$
BEGIN
    RETURN (SELECT COUNT(*) > 0
    FROM users_liked
    WHERE id_recipe = recipe AND id_user = id_use);
END;
